package ar.edu.unju.fi.Punto8;

public class Principal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=4;	
		System.out.println("******Secuencia numerica multiplos de 4******");
		do {
			System.out.print(num+" ");
			num = num +4;
		}while(num<=100);
	}

}
